/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */


export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export interface InsightResult {
	[key: string]: string | number;
}

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError,
	ResultTooLargeError
} from "./IInsightFacade";
import optionController from "./optionController";
import orderController from "./orderController";
import processWhere from "./processWhere";
import {DatasetSections} from "./DatasetSections";
import JSZip from "jszip";
import fs from "fs-extra";
import {DatasetRooms} from "./DatasetRooms";
import processTransformation from "./processTransformation";


/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	private datasets: any;
	constructor() {
		this.datasets = {};
		// if (fs.existsSync("./data")) {
		// 	const files = fs.readdirSync("./data");
		// 	if (files.length > 0) {
		// 		for (const file of files) {
		// 			const dataset = fs.readJsonSync("./data/" + file);
		// 			this.datasets[dataset.id] = dataset;
		// 		}
		// 	}
		// }
		console.log("InsightFacadeImpl::init()");
	}


	public addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		return new Promise<string[]>((resolve, reject) => {
			if (id.match(/^\s*$/) || id.search("_") > 0 || id.length === 0) {
				reject(new InsightError("Invalid ID"));
			}
			if (this.datasets[id]) {
				reject(new InsightError("Duplicate ID"));
			}
			let dataset: any;
			let zip = new JSZip();
			if (kind === InsightDatasetKind.Rooms) {
				dataset = new DatasetRooms(id, kind);
			} else {
				dataset = new DatasetSections(id, kind);
			}
			return zip.loadAsync(content, {base64: true})
				.then(() => {
					if (kind === InsightDatasetKind.Sections) {
						return dataset.courseHelper(zip);
					} else {
						return dataset.buildingsHelper(zip);
					}
				}).then(() => {
					this.datasets[id] = dataset;
					let datasetString = JSON.stringify(dataset);
					if (!fs.existsSync("./data")) {
						fs.mkdir("./data");
					}
					fs.writeFile("./data/" + id + ".json", datasetString, (err: InsightError) => {
						if (err) {
							reject(new InsightError("Error adding file"));
						}
					});
					// console.log(this.datasets);
					// let a = this.datasets.valid.buildings.filter((building: any) => building.rooms.length > 0);
					// console.log(a);
					resolve(Object.keys(this.datasets));
				}).catch((error) => { // implement writing file to disk
					reject(new InsightError("Issue with writing file" + error));
				});
		});
	}

	public removeDataset(id: string): Promise<string> {
		if (id.match(/^\s*$/) || id.search("_") > 0 || id.length === 0) {
			return Promise.reject(new InsightError("Invalid ID"));
		}
		if (this.datasets[id] === undefined) {
			return Promise.reject(new NotFoundError("ID not found"));
		}
		delete this.datasets[id];
		return Promise.resolve(id);
	}

	public validateOptions(optionStatement: any) {
		if (Object.keys(optionStatement).length === 1) {
			if (!optionStatement.COLUMNS) {
				throw new InsightError("Invalid key in OPTIONS");
			}
		} else if (Object.keys(optionStatement).length === 2) {
			if (!optionStatement.COLUMNS || !optionStatement.ORDER) {
				throw new InsightError("Invalid key in OPTIONS");
			}
		} else {
			throw new InsightError("OPTIONS should only contain one or two statements");
		}
		return;
	}

	public findID(query: any): any {
		let queryKey: string;
		let queryType: InsightDatasetKind;
		const validSFields: string[] = ["uuid","id","title","instructor","dept","year","avg","pass","fail","audit"];
		const validRFields: string[] = ["fullname","shortname","number","name","address","lat","lon","seats",
			"type","furniture","href"];
		if(query.OPTIONS.COLUMNS && Array.isArray(query.OPTIONS.COLUMNS) && query.OPTIONS.COLUMNS.length > 0) {
			queryKey = query.OPTIONS.COLUMNS[0];
		} else {
			throw new InsightError("Invalid option");
		}
		let queryKeyIdAndType: string[] = queryKey.split("_");
		if (queryKeyIdAndType.length === 2) {
			queryKey = queryKeyIdAndType[0];
		} else {
			throw new InsightError("Invalid key in query");
		}
		if (validSFields.includes(queryKeyIdAndType[1])) {
			queryType = InsightDatasetKind.Sections;
		} else if (validRFields.includes(queryKeyIdAndType[1])) {
			queryType = InsightDatasetKind.Rooms;
		} else {
			throw new InsightError("Invalid dataset type for query");
		}
		let filteredDataset: Array<{id: string; kind: InsightDatasetKind;}> = [];
		Object.keys(this.datasets).forEach((datasetIndicies: any) => {
			if (this.datasets[datasetIndicies].id === queryKey && this.datasets[datasetIndicies].kind === queryType) {
				let queryObject: {id: string; kind: InsightDatasetKind;} = {
					id: this.datasets[datasetIndicies].id,
					kind: this.datasets[datasetIndicies].kind
				};
				filteredDataset.push(queryObject);
			}
		});
		if (filteredDataset.length !== 1) {
			throw new InsightError("The corresponding dataset is not added");
		}
		return filteredDataset[0];
	}

	public validateQuery (anyQuery: any) {
		if (anyQuery == null) {
			throw new InsightError("Invalid query (null)");
		}
		if (!anyQuery.WHERE || !anyQuery.OPTIONS) {
			throw new InsightError("No WHERE / OPTION statement in query");
		}
		if (Object.keys(anyQuery).length !== 2) {
			if (Object.keys(anyQuery).length === 3 && anyQuery.TRANSFORMATIONS) {
				return;
			} else {
				throw new InsightError("Query should contain two statements or Transformations");
			}
		}
		return;
	}

	public getData(kind: InsightDatasetKind, courseOrRoomData: any, id: string) {
		let data: any[] = [];
		if (kind === InsightDatasetKind.Sections) {
			courseOrRoomData[id].courses.forEach((eachData: any) => {
				if (eachData["sections"].length > 0) {
					data = data.concat(eachData["sections"]);
				}
			});
		} else {
			courseOrRoomData[id].buildings.forEach((eachData: any) => {
				if (eachData.rooms.length > 0) {
					data = data.concat(eachData.rooms);
				}
			});
		}
		return data;
	}

	public performQuery(query: unknown): Promise<InsightResult[]> {
		const anyQuery: any = query;
		let courseOrRoomData: any = this.datasets;
		this.validateQuery(anyQuery);
		const idAndType: {id: string; kind: InsightDatasetKind;} = this.findID(anyQuery);
		const id: string = idAndType.id;
		const kind: InsightDatasetKind = idAndType.kind;
		if (!id || id.match(/^\s*$/) || id.search("_") > 0 || id.length === 0) {
			return Promise.reject(new InsightError("Invalid key"));
		}
		let data: any[] = [];
		data = this.getData(kind, courseOrRoomData, id);
		if (kind === InsightDatasetKind.Rooms) {
			console.log("stop");
		}
		if (kind === InsightDatasetKind.Sections) {
			console.log("stop");
		}
		return new Promise<InsightResult[]>((resolve, reject) => {
			try{
				const whereStatement = anyQuery.WHERE;
				let filteredData: any;
				let transformedData: any;
				if (Object.keys(whereStatement).length === 0) {
					filteredData = data;
				} else if (Object.keys(whereStatement).length !== 1) {
					throw new InsightError("WHERE can only have one key");
				} else {
					filteredData = processWhere(whereStatement, data, id, kind);
				}
				if (anyQuery.TRANSFORMATIONS) {
					transformedData = processTransformation(anyQuery.TRANSFORMATIONS,filteredData,id,kind);
				}
				let finalData: InsightResult[];
				const optionStatement = anyQuery.OPTIONS;
				this.validateOptions(optionStatement,);
				finalData = optionController(optionStatement,filteredData,id,transformedData,anyQuery,kind);
				if (optionStatement.ORDER) {
					finalData = orderController(optionStatement,finalData);
				}
				if (finalData.length > 5000) {
					throw new ResultTooLargeError("More than 5000 results");
				}
				return resolve(finalData);
			} catch (err) {
				return reject(err);
			}
		});
	}

	public listDatasets(): Promise<InsightDataset[]> {
		let listedResult = Object.keys(this.datasets).map((eachID) => {
			return {
				id: this.datasets[eachID].id,
				kind: this.datasets[eachID].kind,
				numRows: this.datasets[eachID].numRows
			};
		});
		return Promise.resolve(listedResult);
	}
}

import InsightFacade from "./InsightFacade";
import {InsightDatasetKind, InsightError, InsightResult} from "./IInsightFacade";

function validateOption(query: any, anyQuery: any, id: string, kind: InsightDatasetKind) {
	const validCourseFields: string[] = ["uuid","id","title","instructor","dept","year","avg","pass","fail","audit"];
	const validRoomFields: string[] = ["shortname", "fullname", "number", "name", "address", "lat", "lon", "seats",
		"type", "furniture", "href"];
	if (anyQuery.TRANSFORMATIONS) {
		let applyKeys = [];
		for (let eachKey of anyQuery.TRANSFORMATIONS.APPLY) {
			applyKeys.push(Object.keys(eachKey)[0]);
		}
		for (const indices in query.COLUMNS) {
			let queryId = query.COLUMNS[indices].split("_")[0];
			if (queryId !== id && !applyKeys.includes(query.COLUMNS[indices])) {
				throw new InsightError("Invalid ID in columns");
			}
		}
		for (const indices in query.COLUMNS) {
			let queryKey = query.COLUMNS[indices].split("_")[1];
			let boolApply = !applyKeys.includes(query.COLUMNS[indices]);
			if (kind === InsightDatasetKind.Sections) {
				if (validCourseFields.find((element) => element === queryKey) === undefined && boolApply) {
					throw new InsightError("Invalid field in COLUMN (should have sections field)");
				}
			} else {
				if (validRoomFields.find((element) => element === queryKey) === undefined && boolApply) {
					throw new InsightError("Invalid keys in COLUMN (should have rooms field)");
				}
			}
			// if (validFields.find((element) => element === queryKey) === undefined && boolApply) {
			// 	throw new InsightError("Invalid keys in COLUMNS");
			// }
		}
	} else {
		for (const indices in query.COLUMNS) {
			if (query.COLUMNS[indices].split("_")[0] !== id) {
				throw new InsightError("Invalid ID in COLUMNS");
			}
		}
		for (const indices in query.COLUMNS) {
			let queryKey = query.COLUMNS[indices].split("_")[1];
			if (kind === InsightDatasetKind.Sections) {
				if (validCourseFields.find((element) => element === queryKey) === undefined) {
					throw new InsightError("Invalid field in COLUMN (should have sections field)");
				}
			} else {
				if (validRoomFields.find((element) => element === queryKey) === undefined) {
					throw new InsightError("Invalid keys in COLUMN (should have rooms field)");
				}
			}

		}
	}
	return;
}
function optionController(query: any,data: any,id: string,transformedData: any, anyQuery: any,
						  kind: InsightDatasetKind): InsightResult[] {
	validateOption(query, anyQuery, id, kind);
	let result: InsightResult[] = [];
	let numOfColumnKey = query.COLUMNS.length;
	if (transformedData && Array.isArray(query.COLUMNS)) {
		let applyKeys = anyQuery.TRANSFORMATIONS.APPLY.map((eachObject: any) => {
			return Object.keys(eachObject)[0];
		});
		let groupKeys = anyQuery.TRANSFORMATIONS.GROUP;
		Object.keys(transformedData).forEach((eachKey: string) => {
			let extractInfo: InsightResult = {};
			query.COLUMNS.forEach((eachColumn: string) => {
				if (applyKeys.includes(eachColumn)) {
					extractInfo[eachColumn] = transformedData[eachKey][eachColumn];
				} else {
					const field = eachColumn.split("_")[1];
					const value = transformedData[eachKey].DATA[0][field];
					extractInfo[eachColumn] = value;
				}
			});
			result.push(extractInfo);
		});
	} else {
		for (let filteredData of data) {
			let extractInfo: InsightResult = {};
			for(let i = 0; i < numOfColumnKey; i++){
				const field = query.COLUMNS[i].split("_")[1];
				const key = query.COLUMNS[i];
				const value = filteredData[field];
				extractInfo[key] = value;
			}
			result.push(extractInfo);
		}
	}
	return result;
}

export default optionController;

import {InsightError, InsightResult} from "./IInsightFacade";

function orderController(query: any, result: InsightResult[]): InsightResult[] {
	if (typeof query.ORDER === "string") {
		if ((query.COLUMNS.find((element: string) => element === query["ORDER"])) === undefined) {
			throw new InsightError("ORDER Key must be in Columns");
		}
		const sortedArray = result.sort((i1: InsightResult, i2: InsightResult) =>
			(i1[query.ORDER] < i2[query.ORDER] ? -1 : 1)
		);
		return sortedArray;
	} else if (typeof query.ORDER === "object") {
		if (!query.ORDER.dir || !query.ORDER.keys || Object.keys(query.ORDER).length !== 2) {
			throw new InsightError("Invalid order");
		}
		for (let key of query.ORDER.keys) {
			if (query.COLUMNS.find((element: string) => element === key) === undefined) {
				throw new InsightError("keys must be in columns");
			}
		}
		let direction: number = 0;
		if (query.ORDER.dir === "UP") {
			direction = 1;
		} else if (query.ORDER.dir === "DOWN") {
			direction = -1;
		} else {
			throw new InsightError("Invalid direction");
		}
		let sortedArray: InsightResult[] = result;
		sortedArray.sort((i1: InsightResult, i2: InsightResult) => {
			for (let key of query.ORDER.keys) {
				if (i1[key] > i2[key]) {
					return direction;
				} else if (i1[key] < i2[key]) {
					return direction * -1;
				} else {
					return 0;
				}
			}
			return 0;
		});
		return sortedArray;
	} else {
		throw new InsightError("Invalid order type");
	}
}
export default orderController;

import InsightFacade from "./InsightFacade";
import {InsightDatasetKind, InsightError} from "./IInsightFacade";
import gtController from "./gtController";
import ltController from "./ltController";
import isController from "./isController";
import eqController from "./eqController";
import orController from "./orController";
import andController from "./andController";

function processWhere(this: any, whereStatement: any, filteredData: any, id: string, kind: InsightDatasetKind): any {
	let processedData: any = filteredData;
	if (!whereStatement.AND && !whereStatement.OR && !whereStatement.NOT && !whereStatement.GT &&
		!whereStatement.LT && !whereStatement.IS && !whereStatement.EQ) {
		throw new InsightError("Invalid key in where");
	}
	if (whereStatement.AND) {
		processedData = andController(whereStatement.AND, processedData, id, kind);
	}
	if (whereStatement.OR) {
		processedData = orController(whereStatement.OR, processedData, id, kind);
	}
	if (whereStatement.NOT) {
		if (Object.keys(whereStatement.NOT).length !== 1) {
			throw new InsightError("NOT can only have one key");
		}
		let dataToExclude: any;
		dataToExclude = processWhere(whereStatement.NOT, processedData, id, kind);
		processedData = processedData.filter(function(eachData: any) {
			if (dataToExclude.indexOf(eachData) > -1){
				return false;
			} else {
				return true;
			}
		});
	}
	if (whereStatement.GT) {
		processedData = gtController(whereStatement.GT, processedData, id, kind);
	}
	if (whereStatement.LT) {
		processedData = ltController(whereStatement.LT, processedData, id, kind);
	}
	if (whereStatement.IS) {
		processedData = isController(whereStatement.IS,processedData, id, kind);
	}
	if (whereStatement.EQ) {
		processedData = eqController(whereStatement.EQ, processedData, id, kind);
	}
	return processedData;
}
export default processWhere;

import {InsightDatasetKind, InsightError} from "./IInsightFacade";

function gtController(query: any, data: any, id: string, kind: InsightDatasetKind): any {
	const numberCourseFields: string[] = ["year","avg","pass","fail","audit"];
	const numberRoomFields: string[] = ["lat","lon","seats"];
	if (Object.keys(query).length !== 1) {
		throw new InsightError("GT can only have one key");
	}
	const gtKey = Object.keys(query)[0];
	const gtValue = query[gtKey];
	const gtId = gtKey.split("_")[0];
	if (gtId !== id) {
		throw new InsightError("Invalid ID in GT");
	}
	const field = gtKey.split("_")[1];
	if (kind === InsightDatasetKind.Sections) {
		if ((numberCourseFields.find((element) => element === field)) === undefined) {
			throw new InsightError("Invalid field in GT (should have sections field)");
		}
	} else {
		if ((numberRoomFields.find((element) => element === field)) === undefined) {
			throw new InsightError("Invalid field in GT (should have rooms field)");
		}
	}
	if (typeof gtValue !== "number") {
		throw new InsightError("Invalid type in GT");
	}
	const filteredData = data.filter(function (eachData: any){
		if (eachData[field] > gtValue) {
			return true;
		} else {
			return false;
		}
	});

	return filteredData;
}
export default gtController;

import InsightFacade from "./InsightFacade";
import {InsightDatasetKind, InsightError} from "./IInsightFacade";

function ltController(query: any, data: any, id: string, kind: InsightDatasetKind): any {
	const numberCourseFields: string[] = ["year","avg","pass","fail","audit"];
	const numberRoomFields: string[] = ["lat","lon","seats"];
	if (Object.keys(query).length !== 1) {
		throw new InsightError("LT can only have one key");
	}
	const ltKey = Object.keys(query)[0];
	const ltValue = query[ltKey];
	const ltId = ltKey.split("_")[0];
	if (ltId !== id) {
		throw new InsightError("Invalid ID in eq");
	}
	const field = ltKey.split("_")[1];
	if (kind === InsightDatasetKind.Sections) {
		if ((numberCourseFields.find((element) => element === field)) === undefined) {
			throw new InsightError("Invalid field in LT (should have sections field)");
		}
	} else {
		if ((numberRoomFields.find((element) => element === field)) === undefined) {
			throw new InsightError("Invalid field in LT (should have rooms field)");
		}
	}
	if (typeof ltValue !== "number") {
		throw new InsightError("Invalid type in LT");
	}
	const filteredData = data.filter(function (eachData: any){
		if (eachData[field] < ltValue) {
			return true;
		} else {
			return false;
		}
	});
	// console.log("filteredData:", filteredData);
	return filteredData;
}
export default ltController;

import InsightFacade from "./InsightFacade";
import {InsightDatasetKind, InsightError} from "./IInsightFacade";
function handleWildcards(isValue: string, field: string, data: any): any {
	let filteredData: any;
	if (isValue === "*") {
		filteredData = data;
	} else if (isValue.substring(1, isValue.length - 1).includes("*")) {
		throw new InsightError("* can only be at first or last character");
	} else if ((isValue[0] === "*") && (isValue[isValue.length - 1] === "*")) {
		const isValueWildCard = isValue.substring(1,isValue.length - 1);
		filteredData = data.filter(function (eachData: any){
			if (eachData[field].indexOf(isValueWildCard) !== -1) {
				return true;
			} else {
				return false;
			}
		});
	} else if ((isValue[0] === "*") && (isValue.length > 1)) {
		const isValueWildCard = isValue.substring(1);
		filteredData = data.filter(function (eachData: any){
			if (eachData[field].endsWith(isValueWildCard)) {
				return true;
			} else {
				return false;
			}
		});
	} else if ((isValue[isValue.length - 1] === "*") && (isValue.length > 1)) {
		const isValueWildCard = isValue.substring(0,isValue.length - 1);
		filteredData = data.filter(function (eachData: any){
